<div class="main-content">    <div class="main-content-inner">        <div class="breadcrumbs ace-save-state" id="breadcrumbs">            <ul class="breadcrumb">                <li>                    <i class="ace-icon fa fa-home home-icon"></i>                    <a href="#">Setup</a>                </li>                <li class="active">Chart Of Account</li>            </ul>            <ul class="breadcrumb pull-right">                <li>                    <a href="<?php echo site_url('listChartOfAccount'); ?>">                        <i class="ace-icon fa fa-list"></i>                        List                    </a>                </li>            </ul>        </div>        <br>        <div class="page-content">            <div class="row">                <div class="col-md-6">                    <form id="publicForm"  action=""  method="post" class="form-horizontal">                        <div class="form-group">                            <label class="col-sm-3 control-label no-padding-right" for="form-field-1">Root Account<span style="color:red;"> ( * )</span></label>                            <div class="col-sm-8">                                <select class="chosen-select form-control" id="rootAccount" onchange="getParentAccountList(this.value)" name="rootAccount" id="form-field-select-3" data-placeholder="Search Root Account">                                    <option value="">  </option>                                    <?php foreach ($rootAccount as $key => $value): ?>                                        <option value="<?php echo $value->chart_id; ?>"><?php echo $value->title; ?></option>                                    <?php endforeach; ?>                                </select>                            </div>                        </div>                        <div class="form-group">                            <label class="col-sm-3 control-label no-padding-right" for="form-field-1">Parent Account</label>                            <div class="col-sm-8">                                <select  onchange="getChildeAccountList(this.value)" class="chosen-select form-control" id="parentAccount" name="parentAccount" id="form-field-select-3" data-placeholder="Search Parent Account">                                    <option value=""></option>                                </select>                            </div>                        </div>                        <div class="form-group">                            <label class="col-sm-3 control-label no-padding-right" for="form-field-1">Child Account</label>                            <div class="col-sm-8">                                <select  onchange="getHeadAccountList(this.value)" class="chosen-select form-control" id="childAccount" name="childAccount" id="form-field-select-3" data-placeholder="Search Child Account">                                    <option value="">  </option>                                </select>                            </div>                        </div>                        <div class="form-group">                            <label class="col-sm-3 control-label no-padding-right" for="form-field-1"> Account Code<span style="color:red;"> ( * )</span></label>                            <div class="col-sm-8">                                <input type="text" value="" id="accountCode"  class="form-control" readonly placeholder="Account Code" name="accountCode"/>                            </div>                        </div>                        <div class="form-group">                            <label class="col-sm-3 control-label no-padding-right" for="form-field-1"> Account Head<span style="color:red;"> ( * )</span></label>                            <div class="col-sm-8">                                <input type="text" onblur="checkDuplicateHead(this.value)" onkeyup="disabledbutton()" id="acc_head" class="form-control" placeholder="Account Head" name="accountHead"/>                                <span id="errorMsg"  style="color:red;display: none;"><i class="ace-icon fa fa-spinner fa-spin orange bigger-120"></i> &nbsp;&nbsp;Account Head already Exits!!Please Type Different Name.</span>                            </div>                        </div>                        <div class="clearfix form-actions" >                            <div class="col-md-offset-4 col-md-8">                                <button onclick="return confirmSwat()"   id="btnDisabled" class="btn btn-info" type="button">                                    <i class="ace-icon fa fa-check bigger-110"></i>                                    Save                                </button>                                &nbsp; &nbsp; &nbsp;                                <button class="btn" type="reset">                                    <i class="ace-icon fa fa-undo bigger-110"></i>                                    Reset                                </button>                            </div>                        </div>                    </form>                </div>                <div class="col-md-6">                    <div id="showTreeList"></div>                </div>            </div><!-- /.col -->        </div><!-- /.row -->    </div><!-- /.page-content --></div><script>    function getHeadAccountList(childID){        showTreeList();        $("#acc_head").val('');        var url = '<?php echo site_url("FinaneController/getHeadCode") ?>';        $.ajax({            type: 'POST',            url: url,            data:{ 'childID': childID},            success: function (data)            {                $("#accountCode").val(data);            }        });    }    function getChildeAccountList(parentId){        showTreeList();        $("#acc_head").val('');        var url = '<?php echo site_url("FinaneController/getChildCode") ?>';        $.ajax({            type: 'POST',            url: url,            data:{ 'parentId': parentId},            success: function (data)            {                $("#accountCode").val(data);            }        });        $("#childAccount").html('<option disabled selected>Search Account Head</option>');        var headTitle = $('#acc_head').val();        var rootAccount = $('#rootAccount').val();        var parentAccount = $('#parentAccount').val();        var childAccount = $('#childAccount').val();        var url = '<?php echo site_url("FinaneController/getChartList") ?>';        $.ajax({            url: url,            type: 'post',            data: {                headTitle: headTitle,                rootAccount:rootAccount,                parentAccount:parentAccount,                childAccount:childAccount            },            success: function(data) {                //$("#parentAccount").html(data);                $('#childAccount').chosen();                $('#childAccount option').remove();                $('#childAccount').append($(data));                $("#childAccount").trigger("chosen:updated");            }        });    }    function showTreeList(){        var url ='<?php echo site_url('FinaneController/getTreeList'); ?>';        var rootId=$("#rootAccount").val();        var parentId=$("#parentAccount").val();        var chaildId=$("#childAccount").val();        $.ajax({            url: url,            type: 'post',            data: {                rootId: rootId,                parentId:parentId,                chaildId:chaildId            },            success: function(data) {                $('#showTreeList').html(data);            }        });    }    function getParentAccountList(rootID){        showTreeList();        $("#acc_head").val('');        var url = '<?php echo site_url("FinaneController/getParentCode") ?>';        $.ajax({            type: 'POST',            url: url,            data:{ 'rootID': rootID},            success: function (data)            {                $("#accountCode").val(data);            }        });        $('#childAccount').chosen();        $('#childAccount option').remove();        $('#childAccount').append($('<option disabled selected>Search Account Head</option>'));        $("#childAccount").trigger("chosen:updated");        $("#parentAccount").html('<option disabled selected>Search Account Head</option>');        var headTitle = $('#acc_head').val();        var rootAccount = $('#rootAccount').val();        var parentAccount = $('#parentAccount').val();        var childAccount = $('#childAccount').val();        var url = '<?php echo site_url("FinaneController/getChartList") ?>';        $.ajax({            url: url,            type: 'post',            data: {                headTitle: headTitle,                rootAccount:rootAccount,                parentAccount:parentAccount,                childAccount:childAccount            },            success: function(data) {                //$("#parentAccount").html(data);                $('#parentAccount').chosen();                $('#parentAccount option').remove();                $('#parentAccount').append($(data));                $("#parentAccount").trigger("chosen:updated");            }        });    }    function disabledbutton(){        // alert("ddd");        $("#btnDisabled").attr('disabled',true);    }    function checkDuplicateHead(){        var headTitle = $('#acc_head').val();        var rootAccount = $('#rootAccount').val();        var parentAccount = $('#parentAccount').val();        var childAccount = $('#childAccount').val();        var url = '<?php echo site_url("FinaneController/checkDuplicateHead") ?>';        $.ajax({            url: url,            type: 'post',            data: {                headTitle: headTitle,                rootAccount:rootAccount,                parentAccount:parentAccount,                childAccount:childAccount            },            success: function(data) {                if(data == 1){                    $("#btnDisabled").attr('disabled',true);                    $("#errorMsg").show();                }else{                    $("#btnDisabled").attr('disabled',false);                    $("#errorMsg").hide();                }            }        });    }</script>