sets/setup.js'); ?>"></script><script>    function deleteOpening(deleteId){        swal({            title: "Are you sure ?",            text: "You won't be able to revert this!",            showCancelButton: true,            confirmButtonColor: '#73AE28',            cancelButtonColor: '#d33',            confirmButtonText: 'Yes',            cancelButtonText: "No",            closeOnConfirm: true,            closeOnCancel: true,            type: 'success'        },        function (isConfirm) {            if (isConfirm) {                window.location.href = "FinaneController/deleteSupplierOpening/" + deleteId;            }else{                return false;            }        });    }    function deleteAllData(){        swal({            title: "Are you sure ?",            text: "You won't be able to revert this!",            showCancelButton: true,            confirmButtonColor: '#73AE28',            cancelButtonColor: '#d33',            confirmButtonText: 'Yes',            cancelButtonText: "No",            closeOnConfirm: true,            closeOnCancel: true,            type: 'success'        },        function (isConfirm) {            if (isConfirm) {                window.location.href = "FinaneController/allSupCusOpeDelete/2";            }else{                return false;            }        });    }</script>