n <?php        if (!empty($categoryID) && $categoryID == 'all') {            echo "selected";        }        ?> value="all">All</option>                                            <?php foreach ($categoryList as $key => $each_info): ?>                                            <option <?php                                            if (!empty($categoryID) && $categoryID == $each_info->category_id): echo "selected";                                            endif;                                                ?> value="<?php echo $each_info->category_id; ?>"><?php echo $each_info->title; ?></option>                                            <?php endforeach; ?>                                    </select>                                </div>                            </div>                        </div>                        <div class="col-md-5">                            <div class="form-group">                                <label class="col-sm-3 control-label no-padding-right" for="form-field-1"> Product</label>                                <div class="col-sm-9">                                    <select  id="productID" name="productid" onchange="getProductPrice(this.value)" class="chosen-select form-control" id="form-field-select-3" data-placeholder="Search by Product">                                        <option <?php                                            if (!empty($productid) && $productid == 'all') {                                                echo "selected";                                            }                                            ?> value="all">All</option>                                    </select>                                </div>                            </div>                        </div>                        <div class="col-md-3">                            <div class="form-group">                                <div class="col-sm-6">                                    <button id="subBtn" class="btn btn-success btn-xs" name="barcode" type="submit">                                        <i class="ace-icon fa fa-barcode bigger-110"></i>                                        Get List                                    </button>                                </div>                                <div class="col-sm-6">                                    <button  onclick="window.print();" style="cursor:pointer;" id="subBtn" class="btn btn-info btn-xs" >                                        <i class="ace-icon fa fa-print bigger-110"></i>                                        Print                                    </button>                                </div>                            </div>                        </div>                    </form>                </div>                <?php                if (!empty($productList) && empty($selectList)):                    ?>                    <form action="" method="POST">                        <div class="col-md-8 col-md-offset-2">                            <table class="table table-bordered">                                <thead>                                    <tr>                                        <td>All Check</td>                                        <td>                                            <div class="checkbox">                                                <label>                                                    <input id="checkAll" class="ace ace-checkbox-2" type="checkbox" value="<?php echo $eachProduct->product_id; ?>">                                                    <span class="lbl">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;All Product</span>                                                </label>                                            </div>                                        </td>                                    </tr>                                </thead>                                <tbody>   