</tr>                                </tfoot>                            </table>                        </div>                    </form>                <?php endif; ?>                <?php                if (count($selectList) >= 1):                    ?>                    <div class="col-md-8 col-md-offset-2">                        <table class="table table-bordered">                            <thead>                                <tr>                                    <td>Product Name</td>                                    <td>Product Barcode</td>                                </tr>                            </thead>                            <?php                            foreach ($selectList as $key => $eachProduct):                                $productInfo = $this->Common_model->getProductInfo($eachProduct);                                ?>                                <tr>                                    <td>                                        <label><?php echo $productInfo->productCat . ' - ' . $productInfo->productName . ' [ ' . $productInfo->brandName . ' ] '; ?></label>                                    </td>                                    <td>                                        <div style="padding:10px; border:1px solid gray;min-width:100px; float:left; font-size:22px;text-align:center;margin:10px;">                                            <b style="font-size:14px;" ><span ><?php echo $productInfo->productName . '[' . $productInfo->brandName . ']'; ?></b></br>                                            <img  src="<?php echo base_url(); ?>/barcode/html/image.php?filetype=PNG&dpi=92&scale=1&rotation=0&font_family=Arial.ttf&font_size=12&text=<?php echo $productInfo->product_code; ?>&thickness=45&checksum=&code=BCGcode39" />                                        </div>                                    </td>                                </tr>                            <?php endforeach; ?>                        </table>                    </div>                <?php endif; ?>            </div><!-- /.col -->        </div><!-- /.row -->    </div><!-- /.page-content --></div><script>    function getProductList(cat_id) {        $.ajax({            type: "POST",            url:baseUrl + "InventoryController/getProductListForBarcode",            data: 'cat_id=' + cat_id,            success: function (data) {                $('#productID').chosen();                $('#productID option').remove();                $('#productID').append($(data));                $("#productID").trigger("chosen:updated");            }        });    }</script><script>    $("#checkAll").change(function () {        $("input:checkbox").prop('checked', $(this).prop("checked"));    });</script>