 showTypeWiseCusList(type){        var url = '<?php echo site_url("SalesController/getCustomerListByType") ?>';        $.ajax({            type: 'POST',            url: url,            data:{ 'type': type},            success: function (data)            {                $(".chosenRefesh").trigger("chosen:updated");                $("#newValue").show();                $("#oldValue").html(data);                $("#oldValue").show();                $('.chosenRefesh').chosen();                $(".chosenRefesh").trigger("chosen:updated");            }        });    }</script>